allFields.index:
function(doc) {
  var i;
  index("default", doc._id);
  // YouTube data field: true if has captions
  if (doc.caption) { 
    index("caption", doc.caption, {
      index: false,
      store: true
    });
  }
  if (doc.captions) {
    for (i = doc.captions.length - 1; i !== 0; --i) {
      index("captions", doc.captions[i], {
        index:false,
        store: true
      });
    }
  }
  if (doc.channelTitle) {
    index("channelTitle", doc.channelTitle, {
      store: true
    });
  }
  if (doc.description) {
    index("description", doc.description, {
      store: true
    });
  }
  if (doc.transcript) {
    index("transcript", doc.transcript, {
      store: true
    });
  }
  if (doc.speakers) {
    for (i = 0; i !== doc.speakers.length; ++i) {
      index("speakers", doc.speakers[i], {
        store: true
      });
    }
  }
  if (doc.title) {
    index("title", doc.title, {
      store: true
    });
  }
  if (doc.id) {
    index("id", doc.id, {
      store: true
    });
  }
  if (doc.viewCount) {
    index("viewCount", parseInt(doc.viewCount), {
      // not_analyzed required for range [m TO n] requests
      index: "not_analyzed", 
      store: true
    });
    // counts need string value
    index("viewCountString", doc.viewCount, {
      facet: true, 
      store: false
    });
  }
  if (doc.publishedAt) {
    index("publishedAt", doc.publishedAt, {
      index: "not_analyzed",
      store: true
    });
  }
  if (doc.publishedAtUnix) {
    index("publishedAtUnix", parseInt(doc.publishedAtUnix), {
      index: "not_analyzed",
      store: true
    });
  }
  if (doc.duration) {
    index("duration", doc.duration, {
      index: "not_analyzed",
      store: true
    });
  }
  if (doc.durationSeconds) {
    index("durationSeconds", parseInt(doc.durationSeconds), {
      index: "not_analyzed",
      store: true
    });
    index("durationSecondsString", doc.durationSeconds, {
      facet: true,
      store: false
    });
  }
  if (doc.commentCount) {
    index("commentCount", parseInt(doc.commentCount), {
      index: "not_analyzed",
      store: true
    });
    index("commentCountString", doc.commentCount, {
      facet: true,
      store: false
    });
  }
  if (doc.favoriteCount) {
    index("favoriteCount", parseInt(doc.favoriteCount), {
      index: "not_analyzed",
      store: true
    });
    index("favoriteCountString", doc.favoriteCount, {
      facet: true,
      store: false
    });
  }
  if (doc.dislikeCount) {
    index("dislikeCount", parseInt(doc.dislikeCount), {
      index: "not_analyzed",
      store: true
    });
    index("dislikeCountString", doc.dislikeCount, {
      facet: true,
      store: false
    });
  }
  if (doc.likeCount) {
    index("likeCount", parseInt(doc.likeCount), {
      index: "not_analyzed",
      store: true
    });
    index("likeCountString", doc.likeCount, {
      facet: true,
      store: false
    });
  }
}

